package fr.lsmbo.msda.spectra.comp.utils;

// TODO: Auto-generated Javadoc
/**
 * Utilities for string values.
 *
 * @author Aromdhani
 */
public class StringsUtils {
	
	/**
	 * Checks if is empty.
	 *
	 * @param str the str
	 * @return the boolean
	 */
	public static Boolean isEmpty(String str) {
		return (str == null || str.trim().isEmpty());
	}
}
